class HtmlOutputer(object):    def __init__(self):        self.html_data = []    def save(self, new_data):        if new_data is not None:            self.html_data.append(new_data)    def output(self):        count = 1        html_file = open('Baike.html', "w")        html_file.write('<html>')        html_file.write('<body>')        html_file.write('<table>')        for data in self.html_data:            html_file.write('<tr>')            html_file.write('<td>%s</td>' % count)            html_file.write('<td>%s</td>' % data['url'].encode('utf-8'))            html_file.write('<td>%s</td>' % data['title'].encode('utf-8'))            html_file.write('<td>%s</td>' % data['content'].encode('utf-8'))            html_file.write('</tr>')            count += 1        html_file.write('</table>')        html_file.write('</body>')        html_file.write('</html>')